mapper.update:
UPDATE guide
SET guide_name =#{guide_name},app_name = #{app_name},registrant = #{registrant}, regist_date = #{regist_date}, download = #{download}, love = #{love}
WHERE guide_id = #{guide_id}

UPDATE guide
SET love = love+1
WHERE guide_id = #{guide_id}

UPDATE guide
SET love = love -1
WHERE guide_id = #{guide_id}

		UPDATE guide
 	SET download = download + 1
 	WHERE guide_id = #{guide_id}
